
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";

function Departments() {
  const departments = [
    {
      name: "COMPUTER SCIENCE",
      description: "The Computer Science department offers cutting-edge programs in software development, algorithms, and data structures.",
      src: "https://cse.mrec.ac.in/Images/seoimages/ComputingFacilities/4.jpg",
      
    },
    {
      name: "AIML ",
      description: "The AIML department focuses on artificial intelligence and machine learning, preparing students for careers in AI research and applications.",
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTTmCD1X9ISpoP0EmPeYMcYfBOT-1kY_iiAVw&s",
    },
    {
      name: "AIDS",
      description: "The AIDS department specializes in advanced information and data science, equipping students with skills in big data analytics and database management.",
      src: "data:image/jpeg;base64,",
    },
    {
      name:"CYBER SECURITY",
      description:"Our Cyber Security department is dedicated to protecting our organization's digital assets and information systems from cyber threats. We implement robust security measures, conduct regular risk assessments, and stay updated on the latest cybersecurity trends to ensure the safety and integrity of our data.",
      src:"data:image/jpeg;base64,",
    },
    {
      name:"CLOUD COMPUTING",
      description:"Our Cloud Computing department focuses on leveraging cloud technologies to enhance our organization's IT infrastructure and services. We design, implement, and manage cloud-based solutions that improve scalability, flexibility, and cost-efficiency, enabling our teams to access resources and applications from anywhere at any time.",
      src:"data:image/jpeg;base64,",
    },
    {
      name: "MECHANICAL",
      description: "The Mechanical Department focuses on the design, analysis, manufacturing, and maintenance of mechanical systems. It encompasses a wide range of sub-disciplines including thermodynamics, fluid mechanics, materials science, and structural analysis. Mechanical engineers work on projects ranging from small components to large machinery and systems, contributing to various industries such as automotive, aerospace, energy, and manufacturing.",
      src: "data:image/jpeg;base64,",
    },
    {
      name: "CIVIL",
      description: "The Civil Department focuses on the design, construction, and maintenance of infrastructure projects such as roads, bridges, and buildings. It combines technical expertise with practical applications to create sustainable and efficient structures that serve communities.",
      src: "data:image/jpeg;base64,",
    },
    {
      name: "ELECTRICAL",
      description: "The Electrical Department is dedicated to the study and application of electricity, electronics, and electromagnetism. It encompasses a wide range of topics including power generation, transmission, distribution, and the design of electrical systems and devices that are integral to modern life.",
      src: "data:image/jpeg;base64,",
    }
  ];
  return (
    <Row xs={1} md={4} className="g-4">
      {departments.map((dept, idx) => (
        <Col key={idx}>
          <Card>
            <Card.Img variant="top" height="150" src={dept.src} />
            <Card.Body>
              <Card.Title>{dept.name}</Card.Title>
              <Card.Text>
                {dept.description}
                
          
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      ))}
    </Row>
  );
}

export default Departments;
